import React, { Component } from 'react';
import './App.css';
import Card from './cardUI/card.js';
import CollegeList from './colleges.json';

class App extends Component {
  constructor() {
    super();
    this.loadMore = this.loadMore.bind(this);
    this.loadColleges = this.loadColleges.bind(this);
    this.state = {
      data: [],
      start: 0,
      end: 10,
      loading: false,
      showLoadMore: true,
      totalItems: CollegeList["colleges"].length,
    };
  }

  componentWillMount() {
    this.loadColleges();
    this.scrollListener = window.addEventListener("scroll", e => {
      this.handleScroll(e);
    });
  }

  loadColleges = () => {
    const { start, end, data } = this.state;
    var dict = CollegeList["colleges"].slice(start, end)
    this.setState({
      data: [...data, ...dict],
    })
  }

  loadMore = () => {
    const { end, totalItems } = this.state;
    this.setState(
      prevState => ({
        start: prevState.start + 10,
        end: prevState.end +10,
      }),
      this.loadColleges
    );
    if (end >= totalItems) {
      this.setState({
        showLoadMore: false
      });
    }
  };

  handleScroll = () => { 
    var lastLi = document.querySelector("ul.container > li:last-child");
    var lastLiOffset = lastLi.offsetTop + lastLi.clientHeight;
    var pageOffset = window.pageYOffset + window.innerHeight;
    if (pageOffset > lastLiOffset) {
          this.loadMore();
        }
  };

  renderData() {
    const { data, showLoadMore } = this.state;
    console.log(window.width)
    return(
      <div>
        <ul className='container college-list'>
        {
          data.map((collegeDetail, index) => (
            <li key ={index}>
              <Card collegeName = {collegeDetail.college_name} />
            </li>
          ))
        }
        </ul>
        {
        showLoadMore 
        ? (
          <button onClick={this.loadMore} >
            Load More
          </button>
        ) : null
        }
        </div>
    );
  }

  render() {
    return (
      this.renderData()
    );
  }
}

export default App;
