import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Tag from './tag.js';
import Rating from './rating.js';

// import CollegeData from '../colleges.json';

class Card extends Component {
  render() {
    const 
    { 
      collegeName,
      collegeImage,
      tags,
      rating,
      ratingRemark,
      ranking,
      promoted,
      nearestPlace,
      landmarks,
    } = this.props;
    return (
      <div className='custom-card'>
        <div className='card-head'>
          <img src = {require(`../${collegeImage}`)} alt='college'></img>
          <div className='overlay'>
            {
              promoted ? 
              (
                <div className='flag-container'>
                  <div className='flag Text-Style-6'>
                    <span>PROMOTED</span>
                  </div>
                </div>
              ) : null

            }
            <div className='rating-container'>
              <Rating rating = {rating} ratingRemark = {ratingRemark} />
            </div>
            <div className='overlay-footer'>
              <div className='tag-container'>
                {
                  tags.map((tag, index) => (
                    <div key={index} className='tag'>
                      <Tag tagData = {tag} />
                    </div>
                  ))
                }
              </div>
              <div className='ranking-container'>
                <div className='ranking-text'>
                  {
                    ranking ? 
                    (
                      '#' + ranking
                    ) : null
                  }
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className='card-body'>
          <div className='card-body-left'>
            <h4 className='m-0 card-title'>
              {collegeName}
            </h4>
            <p className='Text-Style-7 location-details'>
              {
                nearestPlace.map(description =>(
                  <span className='nearest-place'>{description}</span>
                ))
              }
            </p>
            <p >
              <span>93% Match :</span>
              {
                <span>
                  {landmarks.substr(0, landmarks.indexOf(' '))}
                </span> 
              }
            </p>
          </div>
        </div>
      </div>
    );
  }
}

Card.propTypes = {
  collegeName: PropTypes.string,
};

Card.defaultProps = {
  collegeName: 'College',
  collegeImage: '',
};

export default Card;
