import React, { Component } from 'react';
import './App.css';
import Card from './cardUI/card.js';
import CollegeList from './colleges.json';

class App extends Component {
  constructor() {
    super();
    this.loadMore = this.loadMore.bind(this)
    this.state = {
      data: [],
      start: 0,
      end: 10,
      loading: false,
      prevY: 0,
    };
  }

  componentWillMount() {
    this.loadColleges();
  }

  loadColleges() {
    const { start, end } = this.state;
    this.setState({
      data: CollegeList["colleges"]
    })

    var dict = CollegeList["colleges"].slice(start, end)
    for (var key in dict) {
      this.setState({
        data: dict[key],
      })
      // console.log(dict[key])
    }
  }

  loadMore = () => {
    this.setState(
      prevState => ({
        start: prevState.start + 10,
        end: prevState.end +10,
      }),
      this.loadColleges
    );
  };

  render() {
    const { data } = this.state;
    return (
      <div>
        {data.map((collegeDetail, index) => {
          return <div key ={index}>
            <Card collegeName = {collegeDetail.college_name} />
          </div>
        })}
        <button
          onClick={e => {
            this.loadMore;
          }}
        >
          Load More
        </button>
      </div>
    );
  }
}

export default App;
