import React, { Component } from 'react';
import './App.css';
import Card from './cardUI/card.js';
import CollegeList from './colleges.json';

class App extends Component {
  constructor() {
    super();
    this.loadMore = this.loadMore.bind(this);
    this.loadColleges = this.loadColleges.bind(this);
    this.state = {
      data: [],
      start: 0,
      end: 10,
      loading: false,
      prevY: 0,
    };
  }

  componentWillMount() {
    this.loadColleges();
  }

  loadColleges = () => {
    const { start, end } = this.state;

    var dict = CollegeList["colleges"].slice(start, end)
    for (var key in dict) {
      // eslint-disable-next-line
      this.setState( prevState=>({
        data: [...prevState.data, dict[key]],
      }))
      // console.log(dict[key])
    }
  }

  loadMore = () => {
    this.setState(
      prevState => ({
        start: prevState.start + 10,
        end: prevState.end +10,
      }),
      this.loadColleges
    );
  };

  renderData() {
    const { data } = this.state;
    return(
      <div>
        {
          data.map((collegeDetail, index) => (
            <div key ={index}>
              <Card collegeName = {collegeDetail.college_name} />
            </div>
          ))
        }
        <button onClick={this.loadMore} >
          Load More
        </button>
      </div>
    );
  }

  render() {
    return (
      this.renderData()
    );
  }
}

export default App;
